mapper.select:
select line_id,line_name,line_img,line_img,priority,enable_status,create_time,last_edit_time
from tb_head_line

    line_id = #{lineId}
    line_name = #{lineName}
    priority = #{priority}
    enable_status = #{enableStatus}

order by priority desc ;

select line_id,line_name,line_img,line_img,priority,enable_status,create_time,last_edit_time
from tb_head_line
where line_id = #{lineId}
order by priority desc ;
